@echo off\n   setlocal\n\n   set "threshold=80"\n   set "logFile=%~dp0cpu_usage.log"\n\n   typeperf "\Processor(_Total)\% Processor Time" -sc 1 > "%logFile%"\n   set /p cpu=<"%logFile%"\n   set "cpu=%cpu:~-5,2%"\n\n   if %cpu% GEQ %threshold% (\n       powershell -Command "Send-MailMessage -To 'your_email@example.com' -From 'server@example.com' -Subject 'High CPU Usage Alert' -Body 'CPU usage is at %cpu%%' -SmtpServer 'smtp.example.com'"\n   )\n\n   endlocal