@echo off\nsetlocal\n\nrem Define variables\nset INPUT_FILE=your_document.docx\nset OUTPUT_FILE=secure_document.exe\nset PASSWORD=your_password\n\nrem Ensure 7-Zip is in your PATH or specify the full path to 7z.exe\nset SEVENZIP="C:\Program Files\7-Zip\7z.exe"\n\nrem Create an encrypted, password-protected archive\n%SEVENZIP% a -t7z -p%PASSWORD% -mhe=on secure_document.7z %INPUT_FILE%\n\nrem Create a self-extracting executable\n%SEVENZIP% sfx secure_document.7z -o%OUTPUT_FILE%\n\nrem Clean up\ndel secure_document.7z\n\necho Self-extracting archive created: %OUTPUT_FILE%\nendlocal