@echo off\n   setlocal\n   set exeName=yourAppName.exe\n\n   rem Start the application\n   start "" "%exeName%"\n\n   rem Wait for a moment to let the process start\n   timeout /t 2 /nobreak >nul\n\n   rem Get the process ID (PID) of the application\n   for /f "tokens=2 delims==; " %%A in ('wmic process where "name='%exeName%'" get ProcessId /value') do set PID=%%A\n\n   rem Set the process priority to normal\n   wmic process where "ProcessId=%PID%" CALL setpriority "normal"