#!/bin/bash\n\n# Source and destination directories\nSOURCE_DIR="/cygdrive/c/images"\nDEST_DIR="/cygdrive/d/media"\n\n# Find all .jpg files in the source directory\nfind "$SOURCE_DIR" -type f -name "*.jpg" | while read -r file; do\n  # Determine the relative path of the file\n  rel_path="${file#$SOURCE_DIR/}"\n  \n  # Determine the destination directory for the current file\n  dest_dir="$DEST_DIR/$(dirname "$rel_path")"\n  \n  # Create the destination directory if it doesn't exist\n  mkdir -p "$dest_dir"\n  \n  # Move the file to the destination directory\n  mv "$file" "$dest_dir"\ndone