# Export the public key from GPG\ngpg --export-ssh-key you@example.com > publickey.ssh\n\n# Export the private key from GPG\ngpg --export-secret-keys --armor you@example.com > privatekey.asc\n\n# Import the private key into an OpenSSH format (requires OpenSSH 7.8+)\nssh-keygen -i -f publickey.ssh -m PKCS8 > id_rsa.pub\nssh-keygen -i -f privatekey.asc -m PKCS8 > id_rsa\n\n# Ensure appropriate permissions\nchmod 600 id_rsa\nchmod 644 id_rsa.pub