#!/bin/bash\n\n# Variables\nREPO_PATH="/path/to/your/repo"\nSTAGING_PATH="/path/to/staging"\nPROD_PATH="/path/to/production"\nBRANCH="main"\nSSH_USER="user"\nSSH_HOST="your.server.com"\nSSH_PATH="/var/www/html"\n\n# Step 1: Pull the latest code from the repository\ncd $REPO_PATH\ngit checkout $BRANCH\ngit pull origin $BRANCH\n\n# Step 2: Run tests (optional but recommended)\n# ./run_tests.sh\n\n# Step 3: Sync files to the staging server\nrsync -avz --delete --exclude='*.git' $REPO_PATH/ $STAGING_PATH\n\n# Step 4: After successful testing, deploy to the production server\nrsync -avz --delete --exclude='*.git' $STAGING_PATH/ $SSH_USER@$SSH_HOST:$SSH_PATH\n\necho "Deployment completed successfully."