@echo off\nREM Replace 'certfile.pfx' with the path to your certificate file\nREM Replace 'password' with the password for your PFX file\n\nset CERTFILE=certfile.pfx\nset PASSWORD=password\n\nREM Import the certificate\npfximprt.exe -f %CERTFILE% -p %PASSWORD%\n\nREM Check if the import was successful\nif %errorlevel% neq 0 (\n    echo "Certificate import failed!"\n) else (\n    echo "Certificate import successful!"\n)