@echo off\nsetlocal\n\nREM Read the FTP server address from the registry\nfor /f "tokens=3" %%A in ('reg query "HKCU\Software\YourApp\FTP" /v Server') do set FTP_SERVER=%%A\n\nREM Read the FTP username from the registry\nfor /f "tokens=3" %%A in ('reg query "HKCU\Software\YourApp\FTP" /v Username') do set FTP_USERNAME=%%A\n\nREM Read the FTP password from the registry\nfor /f "tokens=3" %%A in ('reg query "HKCU\Software\YourApp\FTP" /v Password') do set FTP_PASSWORD=%%A\n\nREM Create the FTP script\n(\necho open %FTP_SERVER%\necho user %FTP_USERNAME% %FTP_PASSWORD%\necho binary\necho put yourfile.txt\necho quit\n) > ftpscript.txt\n\nREM Execute the FTP command using the script\nftp -s:ftpscript.txt\n\nREM Clean up\ndel ftpscript.txt\n\nendlocal