@echo off\nsetlocal\n\n:: Get the current date in yyyy-MM-dd format\nfor /f "tokens=2 delims==" %%I in ('"wmic os get localdatetime /value | findstr LocalDateTime"') do set currentDate=%%I\nset year=%currentDate:~0,4%\nset month=%currentDate:~4,2%\nset day=%currentDate:~6,2%\nset formattedDate=%year%-%month%-%day%\n\n:: Define source and archive folder paths\nset sourceFile=source.log\nset archiveFolder=C:\path\to\archive\folder\n\n:: Copy the file with the timestamped filename\ncopy %sourceFile% %archiveFolder%\%formattedDate%.log\n\nendlocal