@echo off\n    REM Set paths\n    set TLBIMP_PATH=%SystemRoot%\Microsoft.NET\Framework\v2.0.50727\tlbimp.exe\n    set COM_DLL=path\to\MyLogic.dll\n    set INTEROP_DLL=path\to\output\Interop.MyLogicLib.dll\n    set TEMP_INTEROP_DLL=path\to\temp\Interop.MyLogicLib.tmp.dll\n\n    REM Extract version from the COM DLL\n    for /f "tokens=2 delims==" %%i in ('wmic datafile where "name='%COM_DLL:'=\\%'" get version /value') do set COM_VERSION=%%i\n\n    REM Generate interop assembly with the correct version\n    %TLBIMP_PATH% %COM_DLL% /out:%TEMP_INTEROP_DLL% /asmversion:%COM_VERSION%\n\n    REM Replace the auto-generated interop DLL with the new one\n    move /Y %TEMP_INTEROP_DLL% %INTEROP_DLL%