@echo off\n   setlocal\n   :: Set the date format for the filename (YYYY-MM-DD format)\n   set "datetime=%date:~10,4%-%date:~4,2%-%date:~7,2%_%time:~0,2%-%time:~3,2%-%time:~6,2%"\n   :: Remove spaces from the time string\n   set "datetime=%datetime: =0%"\n   :: Define the backup directory (change this to your desired path)\n   set "backup_dir=C:\EventLogBackups"\n   :: Ensure the backup directory exists\n   if not exist "%backup_dir%" mkdir "%backup_dir%"\n   :: Export the Application log\n   wevtutil epl Application "%backup_dir%\ApplicationLog_%datetime%.evtx"\n   endlocal