#!/bin/bash\n\n# Define the mount point and the source\nMOUNT_POINT="/mnt/remote1"\nSOURCE="root@remote1:/"\n\n# Check if the mount point is mounted\nif mountpoint -q "$MOUNT_POINT"; then\n    echo "$MOUNT_POINT is mounted. Proceeding with rsync..."\n    rsync -avz --delete --exclude=proc --exclude=sys "$SOURCE" "$MOUNT_POINT" \n    RETVAL=$?\n    if [ $RETVAL -eq 0 ]; then\n        echo "Backup completed successfully."\n    else\n        echo "Backup failed with return value: $RETVAL"\n    fi\nelse\n    echo "Error: $MOUNT_POINT is not mounted. Aborting backup."\n    exit 1\nfi