#!/bin/bash\n\n# Function to add .jpg extension to files without an extension\nadd_extension() {\n  for file in "$1"/*; do\n    if [ -d "$file" ]; then\n      # If the file is a directory, recursively call this function\n      add_extension "$file"\n    elif [ -f "$file" ] && [[ "$file" != *.* ]]; then\n      # If the file is a regular file and does not have an extension, add .jpg\n      mv "$file" "$file.jpg"\n    fi\n  done\n}\n\n# Start the recursive process from the current directory or specify a directory as an argument\nadd_extension "${1:-.}"