# Locate the private key\n   $cert = Get-ChildItem -Path cert:\LocalMachine\My | Where-Object { $_.Subject -match "CN=your_certificate_common_name" }\n   $keyPath = $cert.PrivateKey.CspKeyContainerInfo.UniqueKeyContainerName\n   $fullPath = "C:\ProgramData\Microsoft\Crypto\RSA\MachineKeys\" + $keyPath\n\n   # Grant access to the private key\n   icacls $fullPath /grant "NT AUTHORITY\NETWORK SERVICE":R