#!/bin/bash\n\n# Configuration\nSITE_DIR="/path/to/your/php/site"\nBACKUP_DIR="/path/to/backup"\nDB_NAME="your_database_name"\nDB_USER="your_database_user"\nDB_PASS="your_database_password"\nFTP_HOST="ftp.yourbackupserver.com"\nFTP_USER="ftp_username"\nFTP_PASS="ftp_password"\nDATE=$(date +'%Y-%m-%d')\n\n# Create backup directory if it doesn't exist\nmkdir -p $BACKUP_DIR\n\n# Backup site files\ntar -czf $BACKUP_DIR/site_backup_$DATE.tar.gz -C $SITE_DIR .\n\n# Backup MySQL database\nmysqldump -u $DB_USER -p$DB_PASS $DB_NAME > $BACKUP_DIR/db_backup_$DATE.sql\n\n# Upload to remote FTP server\nftp -inv $FTP_HOST <<EOF\nuser $FTP_USER $FTP_PASS\nbinary\nput $BACKUP_DIR/site_backup_$DATE.tar.gz\nput $BACKUP_DIR/db_backup_$DATE.sql\nbye\nEOF\n\n# Clean up local backups (optional)\nrm $BACKUP_DIR/site_backup_$DATE.tar.gz\nrm $BACKUP_DIR/db_backup_$DATE.sql\n\necho "Backup completed and uploaded to FTP server."