#!/bin/bash\n\n# Define your repositories\ndeclare -A repos\nrepos=(\n    ["proj1"]="https://github.com/username/proj1.git"\n    ["proj2"]="https://github.com/username/proj2.git"\n)\n\n# Navigate to your main repository\ncd /path/to/main_repo\n\n# Pull the latest changes from each subtree\nfor repo in "${!repos[@]}"; do\n    git subtree pull --prefix=projects/$repo ${repos[$repo]} master --squash\ndone\n\n# Commit the changes\ngit commit -am "Update all subtrees"\n\n# Push to backup\ngit push backup master