#!/bin/bash\n\n# Ensure necessary tools are installed\nif ! command -v du &> /dev/null || ! command -v gnuplot &> /dev/null || ! command -v convert &> /dev/null; then\n    echo "Please install 'du', 'gnuplot', and 'ImageMagick' to use this script."\n    exit 1\nfi\n\n# Generate disk usage data\ndu -sh * | sort -hr > disk_usage.txt\n\n# Create a gnuplot script to generate the graph\ncat << 'EOF' > disk_usage_plot.plt\nset terminal png size 800,600\nset output 'disk_usage.png'\nset boxwidth 0.5\nset style fill solid\nset ylabel "Size (in human-readable format)"\nset xlabel "Directories/Files"\nset title "Disk Usage"\nset grid ytics\nplot 'disk_usage.txt' using 2:xtic(1) with boxes notitle\nEOF\n\n# Run the gnuplot script\ngnuplot disk_usage_plot.plt\n\n# Clean up\nrm disk_usage.txt disk_usage_plot.plt\n\necho "Disk usage graph generated as disk_usage.png"