#!/bin/bash\n\n# Set threshold for disk space usage (e.g., 80%)\nTHRESHOLD=80\n\n# Get the current disk space usage percentage\nUSAGE=$(df / | grep / | awk '{ print $5 }' | sed 's/%//g')\n\n# Check if usage exceeds the threshold\nif [ "$USAGE" -gt "$THRESHOLD" ]; then\n    echo "Disk space usage is above $THRESHOLD%. Current usage: $USAGE%" | mail -s "Disk Space Alert" your-email@example.com\nfi