#!/bin/bash\n\n# Path to the JDK\nJAVA_HOME=/path/to/jdk\n\n# Application module name\nMODULE_NAME=your.module.name\n\n# Output directory for custom runtime\nOUTPUT_DIR=custom-runtime\n\n# Determine OS\nOS=$(uname -s)\n\ncase "$OS" in\n    Linux*)     OS_TYPE=linux;;\n    Darwin*)    OS_TYPE=macos;;\n    CYGWIN*|MINGW*|MSYS*) OS_TYPE=windows;;\n    *)          echo "Unsupported OS"; exit 1;;\nesac\n\n# Use jdeps to determine modules\nMODULES=$(jdeps --print-module-deps --module-path mods --add-modules $MODULE_NAME)\n\n# Create custom runtime image\njlink --module-path $JAVA_HOME/jmods:mods --add-modules $MODULES --output $OUTPUT_DIR/$OS_TYPE\n\necho "Custom runtime image created at $OUTPUT_DIR/$OS_TYPE"