#!/bin/bash\n\n# Variables\nOLDFILE="yourfile.txt"\nNEWLINE="This is the new line to insert"\nTEMPFILE=$(mktemp)\n\n# Get the total number of lines in the file\nTOTAL_LINES=$(wc -l < "$OLDFILE")\n\n# Calculate the position to insert the new line (before the last line)\nINSERT_POSITION=$((TOTAL_LINES - 1))\n\n# Combine the parts and insert the new line\nhead -n "$INSERT_POSITION" "$OLDFILE" > "$TEMPFILE"\necho "$NEWLINE" >> "$TEMPFILE"\ntail -n +$((INSERT_POSITION + 1)) "$OLDFILE" >> "$TEMPFILE"\n\n# Optionally, replace the original file with the new file\nmv "$TEMPFILE" "$OLDFILE"\n\necho "Line inserted successfully."