#!/bin/bash\n\n# Check if the correct number of arguments are provided\nif [ "$#" -ne 2 ]; then\n  echo "Usage: $0 search_string replace_string"\n  exit 1\nfi\n\nsearch_string=$1\nreplace_string=$2\n\n# Escape special characters for sed\nescaped_search_string=$(printf '%s\n' "$search_string" | sed -e 's/[\/&]/\\&/g')\nescaped_replace_string=$(printf '%s\n' "$replace_string" | sed -e 's/[\/&]/\\&/g')\n\n# Find and replace recursively in the current directory\nfind . -type f -exec sed -i.bak "s/$escaped_search_string/$escaped_replace_string/g" {} +\n\n# Optionally, remove backup files\n# find . -type f -name "*.bak" -exec rm {} +\n\necho "Replacement complete."