#!/bin/bash\n\n   # List of application URLs to check\n   APP_URLS=("http://app1.example.com" "http://app2.example.com")\n\n   # Email settings\n   EMAIL_SUBJECT="Application Down Alert"\n   EMAIL_RECIPIENTS="devteam@example.com"\n\n   # Function to check application status\n   check_app_status() {\n     local url=$1\n     local response=$(curl -s -o /dev/null -w "%{http_code}" $url)\n     \n     if [[ "$response" -ne 200 ]]; then\n       echo "Application at $url is down (HTTP Status: $response)"\n       send_email "$url" "$response"\n     else\n       echo "Application at $url is up (HTTP Status: $response)"\n     fi\n   }\n\n   # Function to send email notification\n   send_email() {\n     local url=$1\n     local status=$2\n     local message="The application at $url is down with HTTP status code $status."\n     \n     echo "$message" | mail -s "$EMAIL_SUBJECT" "$EMAIL_RECIPIENTS"\n   }\n\n   # Check each application URL\n   for url in "${APP_URLS[@]}"; do\n     check_app_status "$url"\n   done