#!/bin/bash\n\ninput_file="input.csv"\noutput_file="output.txt"\n\n# Initialize the output file\n> "$output_file"\n\n# Read each line from the input file\nwhile IFS= read -r line; do\n    # Extract the voltage value using grep and awk\n    voltage=$(echo "$line" | grep -oP 'voltage: \K[0-9.]+(?=V)')\n    \n    # If no voltage is found, set it to an empty line\n    if [ -z "$voltage" ]; then\n        echo "" >> "$output_file"\n    else\n        echo "$voltage" >> "$output_file"\n    fi\ndone < "$input_file"