# Step 1: Navigate to your MediaWiki installation directory\ncd /path/to/mediawiki\n\n# Step 2: Download the VisualEditor extension\ngit clone https://gerrit.wikimedia.org/r/mediawiki/extensions/VisualEditor.git extensions/VisualEditor\n\n# Step 3: Download the required Parsoid service\ngit clone https://gerrit.wikimedia.org/r/mediawiki/services/parsoid.git\n\n# Step 4: Add the following lines to your LocalSettings.php\nwfLoadExtension( 'VisualEditor' );\n$wgDefaultUserOptions['visualeditor-enable'] = 1;\n$wgVirtualRestConfig['modules']['parsoid'] = array(\n    'url' => 'http://localhost:8000',\n    'domain' => 'localhost',\n    'prefix' => 'localhost'\n);\n\n# Step 5: Start the Parsoid service\ncd parsoid\nnpm install\nnode bin/server.js