#!/bin/bash\n\nfor file in *.txt; do\n  # Extract the datetime part from the filename\n  datetime=$(echo "$file" | grep -oP '^\d{12}')\n  \n  # Format the datetime to match 'touch' requirements (YYYYMMDDhhmm)\n  if [[ $datetime =~ ^[0-9]{12}$ ]]; then\n    formatted_datetime=$(date -d "${datetime:0:8} ${datetime:8:4}" +%Y%m%d%H%M)\n\n    # Use 'touch' to set the mtime of the file\n    touch -t "$formatted_datetime" "$file"\n    echo "Updated mtime for $file to $formatted_datetime"\n  else\n    echo "Skipping $file, no valid datetime found in filename."\n  fi\ndone