# Install Composer\ncurl -sS https://getcomposer.org/installer | php\nsudo mv composer.phar /usr/local/bin/composer\n\n# Download Magento using Composer\ncomposer create-project --repository-url=https://repo.magento.com/ magento/project-community-edition=2.4.x magento2\n\n# Set up the web server (Apache/Nginx) and MySQL\n# Follow the installation wizard or use the command line installer\nphp bin/magento setup:install \\n--base-url=http://yourdomain.com/ \\n--db-host=localhost \\n--db-name=magento \\n--db-user=magento \\n--db-password=yourpassword \\n--admin-firstname=Admin \\n--admin-lastname=User \\n--admin-email=admin@example.com \\n--admin-user=admin \\n--admin-password=admin123 \\n--language=en_US \\n--currency=USD \\n--timezone=America/Chicago \\n--use-rewrites=1