#!/bin/bash\n\n# Function to replace fancy characters with plain ASCII equivalents\nreplace_fancy_characters() {\n    sed -e "s/…/.../g" \\n        -e "s/“/\"/g" \\n        -e "s/”/\"/g" \\n        -e "s/‘/'/g" \\n        -e "s/’/'/g" \\n        -e "s/—/--/g" \\n        -e "s/–/-/g" \\n        -e "s/„/\"/g" \\n        -e "s/´/'/g" \\n        -e "s/«/\"/g" \\n        -e "s/»/\"/g" \\n        -e "s/‹/</g" \\n        -e "s/›/>/g" \\n        -e "s/“/\"/g" \\n        -e "s/”/\"/g" \\n        -e "s/‘/'/g" \\n        -e "s/’/'/g" \\n        -e "s/—/--/g" \\n        -e "s/–/-/g" \\n        -e "s/„/\"/g" \\n        -e "s/´/'/g" \\n        -e "s/«/\"/g" \\n        -e "s/»/\"/g" \\n        -e "s/‹/</g" \\n        -e "s/›/>/g"\n}\n\n# Convert file encoding to ASCII and replace fancy characters\nconvert_to_ascii() {\n    input_file="$1"\n    output_file="$2"\n\n    iconv -f UTF-8 -t ASCII//TRANSLIT "$input_file" | replace_fancy_characters > "$output_file"\n}\n\n# Iterate over all text files in the directory and convert them\nfor file in *.txt; do\n    convert_to_ascii "$file" "ascii_$file"\ndone