#!/bin/bash\n\n# Variables\nDUMP_FILE="sybase_dump_file.dmp.gz"\nCHECKSUM_FILE="sybase_dump_file.md5"\n\n# Verify MD5 checksum\nif md5sum -c "$CHECKSUM_FILE"; then\n    echo "Checksum verification passed."\nelse\n    echo "Checksum verification failed."\n    exit 1\nfi\n\n# Check gzip integrity\nif gunzip -t "$DUMP_FILE"; then\n    echo "Gzip file integrity check passed."\nelse\n    echo "Gzip file integrity check failed."\n    exit 1\nfi\n\necho "File is verified and intact."