#!/bin/bash\n\n# Check if exactly one argument is provided\nif [ "$#" -ne 1 ]; then\n  echo "Usage: $0 <id-number>"\n  exit 1\nfi\n\n# Check if the argument is numeric\nif ! [[ "$1" =~ ^[0-9]+$ ]]; then\n  echo "Error: id-number must be a numeric value."\n  exit 1\nfi\n\n# Define the base directories\nbase_dirs=("~/myfolder1" "~/myfolder2" "~/myfolder3")\n\n# Check if the directories exist before attempting to remove them\nfor base_dir in "${base_dirs[@]}"; do\n  full_path="${base_dir}/$1"\n  if [ ! -d "$full_path" ]; then\n    echo "Error: Directory $full_path does not exist."\n    exit 1\n  fi\ndone\n\n# If all checks pass, proceed with deletion\nrm -rf ~/myfolder1/$1/anotherfolder\nrm -rf ~/myfolder2/$1/yetanotherfolder\nrm -rf ~/myfolder3/$1/thisisafolder\n\necho "Folders successfully removed."