@echo off\nsetlocal\n\nset "process_name=firefox.exe"\nset "found_process=0"\n\nfor /f "tokens=1 delims= " %%A in ('tasklist /fi "imagename eq %process_name%" /nh') do (\n    if /i "%%A"=="%process_name%" (\n        set "found_process=1"\n    )\n)\n\nif %found_process%==1 (\n    echo %process_name% is running.\n    REM Take action if the process is running\n) else (\n    echo %process_name% is not running.\n    REM Take action if the process is not running\n)\n\nendlocal