# Dockerfile to create a minimal environment for compiling and running C code\nFROM gcc:latest\n\n# Create a user with limited privileges\nRUN useradd -ms /bin/bash sandboxuser\n\n# Switch to the sandbox user\nUSER sandboxuser\n\n# Set working directory\nWORKDIR /home/sandboxuser\n\n# Copy the script to compile and run the code\nCOPY run_code.sh /home/sandboxuser/run_code.sh\n\n# Make the script executable\nRUN chmod +x /home/sandboxuser/run_code.sh\n\nENTRYPOINT ["/home/sandboxuser/run_code.sh"]