#!/bin/bash\n\n# Check if at least two files are provided\nif [ "$#" -lt 2 ]; then\n    echo "Usage: $0 file1 file2 [file3 ... fileN]"\n    exit 1\nfi\n\n# Create a temporary directory for intermediate sorted files\ntmpdir=$(mktemp -d)\ntrap "rm -rf $tmpdir" EXIT\n\n# Sort each file and store the result in the temporary directory\nfor file in "$@"; do\n    sort "$file" > "$tmpdir/$(basename "$file").sorted"\ndone\n\n# Find the common lines between all sorted files\ncomm -12 "$tmpdir/$(basename "$1").sorted" "$tmpdir/$(basename "$2").sorted" > "$tmpdir/common"\n\nfor file in "${@:3}"; do\n    comm -12 "$tmpdir/common" "$tmpdir/$(basename "$file").sorted" > "$tmpdir/common.tmp"\n    mv "$tmpdir/common.tmp" "$tmpdir/common"\ndone\n\n# Display the common lines\ncat "$tmpdir/common"