#!/bin/bash\n\n# Define the base directory\nBASE_DIR="/path/to/tmp"\n\n# Define the desired permissions\nPERMISSIONS="755"\n\n# Change permissions for all existing directories under BASE_DIR\nfind "$BASE_DIR" -type d -exec chmod "$PERMISSIONS" {} \;\n\n# Optional: Add new component directories if they don't exist\nCOMPONENTS=("api" "email2sms" "scheduled_sending" "billing")\n\nfor COMPONENT in "${COMPONENTS[@]}"; do\n  if [ ! -d "$BASE_DIR/$COMPONENT" ]; then\n    mkdir "$BASE_DIR/$COMPONENT"\n    chmod "$PERMISSIONS" "$BASE_DIR/$COMPONENT"\n  fi\ndone\n\necho "Permissions set to $PERMISSIONS for all directories under $BASE_DIR"