#!/bin/bash\n\nPOD_NAME=$(hostname)\nNAMESPACE="default"\nTOKEN=$(cat /var/run/secrets/kubernetes.io/serviceaccount/token)\nAPI_SERVER="https://kubernetes.default.svc"\n\nIS_ACTIVE=$(curl -s --cacert /var/run/secrets/kubernetes.io/serviceaccount/ca.crt \\n    -H "Authorization: Bearer $TOKEN" \\n    "$API_SERVER/api/v1/namespaces/$NAMESPACE/pods/$POD_NAME" | jq -r '.status.phase')\n\nif [ "$IS_ACTIVE" == "Running" ]; then\n    echo "This is the active node."\n    # Trigger pager alert\nelse\n    echo "This is the inactive node."\n    # Send email alert\nfi