#!/bin/bash\n\n# Define the input file\ninput_file="input.txt"\n\n# Define the substring to be replaced and the replacement\nsubstring="test"\nreplacement="replaced"\n\n# Use 'tac' to reverse the lines, 'sed' to replace the first occurrence, and 'tac' again to reverse the lines back\noutput_string=$(tac "$input_file" | sed "0,/$substring/{s/$substring/$replacement/}" | tac)\n\n# Print the result\necho "$output_string"