#!/bin/bash\n\n# Connect to the Oracle database and get the alert log directory\nalert_log_dir=$(sqlplus -s / as sysdba <<EOF\nSET PAGESIZE 0 FEEDBACK OFF VERIFY OFF HEADING OFF ECHO OFF\nSELECT VALUE FROM V\$DIAG_INFO WHERE NAME = 'Diag Trace';\nEXIT;\nEOF\n)\n\n# Define the alert log file name\nalert_log_file="$alert_log_dir/alert_$(hostname | cut -d. -f1).log"\n\n# Tail the alert log\ntail -f "$alert_log_file"