#!/bin/bash\n\n# Directory containing the images\nimage_dir="images"\n\n# Loop through each image file in the directory\nfind "$image_dir" -type f -name '*.jpg' | while read -r file; do\n    # Extract the filename from the full path\n    filename=$(basename "$file")\n    \n    # Get the first three characters of the filename\n    first_char=${filename:0:1}\n    second_char=${filename:1:1}\n    third_char=${filename:2:1}\n    \n    # Create the new directory path\n    new_dir="$image_dir/$first_char/$second_char/$third_char"\n    \n    # Create the directories if they don't exist\n    mkdir -p "$new_dir"\n    \n    # Move the file to the new directory\n    mv "$file" "$new_dir/$filename"\ndone