#!/bin/bash\n\n# Save this script as run_cpp.sh and make it executable with chmod +x run_cpp.sh\n\n# Check if a file name is provided\nif [ -z "$1" ]; then\n  echo "Usage: $0 <source_file.cpp>"\n  exit 1\nfi\n\n# Compile the C++ code\ng++ -o output "$1" \n\n# Check if compilation was successful\nif [ $? -eq 0 ]; then\n  # Run the compiled code\n  ./output\nelse\n  echo "Compilation failed."\nfi