#!/bin/bash\n\n     THRESHOLD=100  # Set your IOPS threshold here\n\n     while true; do\n       IOPS=$(iostat -d 1 2 | awk '/sda/ {print $4 + $5}' | tail -n1)  # Adjust 'sda' to your disk\n       if (( $(echo "$IOPS > $THRESHOLD" | bc -l) )); then\n         echo "IOPS threshold exceeded: $IOPS IOPS" | mail -s "IOPS Alert" your-email@example.com\n       fi\n       sleep 60  # Check every minute\n     done