#!/bin/bash\n\n# Directory containing the zip files\nzip_dir="files"\n\n# Loop through each zip file in the directory\nfor zip_file in "$zip_dir"/*.zip; do\n    # Get the base name of the zip file (without extension)\n    base_name=$(basename "$zip_file" .zip)\n    \n    # Create a directory with the same name as the zip file\n    mkdir -p "$zip_dir/$base_name"\n    \n    # Unzip only the image files into the created directory\n    unzip "$zip_file" "*.jpg" "*.png" "*.gif" -d "$zip_dir/$base_name"\ndone