#!/bin/bash\n\n   DIRECTORY="recordings"\n\n   inotifywait -m -r -e create --format '%w%f' "${DIRECTORY}" | while read NEWFILE\n   do\n       if [[ "$NEWFILE" == *.jpg ]]; then\n           # URL encode the file path\n           ENCODED_PATH=$(php -r "echo urlencode('${NEWFILE}');")\n           # Trigger the PHP script\n           curl -s "http://yourserver.com/newimage.php?location=${ENCODED_PATH}" > /dev/null\n       fi\n   done