#!/bin/bash\n\n# Specify the root directory of your working copy\nWORKING_COPY_ROOT="/path/to/your/working/copy"\n\n# Generate the patch using Subclipse and save it to a file\n# (Assume the patch file is named "subclipse_patch.patch")\nPATCH_FILE="subclipse_patch.patch"\n\n# Create a new patch file with relative paths\nRELATIVE_PATCH_FILE="relative_patch.patch"\n\n# Replace the absolute paths with relative paths\nsed "s|$WORKING_COPY_ROOT/||g" "$PATCH_FILE" > "$RELATIVE_PATCH_FILE"\n\necho "Relative patch created: $RELATIVE_PATCH_FILE"