#!/bin/bash\n\n# Loop through each file in the current directory\nfor file in *; do\n  # Check if it's a file (not a directory)\n  if [ -f "$file" ]; then\n    # Create the new filename with capitalization\n    newfile=$(echo "$file" | tr '[:lower:]' '[:upper:]')\n    \n    # Check if the new filename is different from the old one\n    if [ "$file" != "$newfile" ]; then\n      # Move the file using svn mv\n      svn mv "$file" "$newfile"\n    fi\n  fi\ndone\n\n# Commit the changes with a message\nsvn commit -m "Capitalized filenames"