# Create a new VM for macOS\nVBoxManage createvm --name "macOS" --ostype "MacOS_64" --register\nVBoxManage modifyvm "macOS" --memory 4096 --vram 128 --cpus 2 --nic1 nat\nVBoxManage createhd --filename "macOS.vdi" --size 40000\nVBoxManage storagectl "macOS" --name "SATA Controller" --add sata --controller IntelAhci\nVBoxManage storageattach "macOS" --storagectl "SATA Controller" --port 0 --device 0 --type hdd --medium "macOS.vdi"\nVBoxManage storageattach "macOS" --storagectl "SATA Controller" --port 1 --device 0 --type dvddrive --medium "/path/to/macos_installer.iso"\nVBoxManage startvm "macOS"