#!/bin/bash\n\n# Compile with debugging information\ngcc -g -o yourfile.o yourfile.c\n\n# Generate control flow graph\ncflow --cpp='gcc -E' yourfile.c > cflow_output.txt\n\n# Parse cflow output to extract function calls and addresses\ngrep -E "^[^ ]+.*\(" cflow_output.txt | while read -r line; do\n    function_call=$(echo "$line" | awk '{print $1}')\n    if addr=$(nm yourfile.o | grep " T $function_call" | awk '{print $1}'); then\n        echo "Function call: $function_call"\n        echo "Address: $addr"\n        addr2line -e yourfile.o $addr\n    fi\ndone