#!/bin/bash\n\n# MySQL credentials\nUSER="your_username"\nPASSWORD="your_password"\nDATABASE="your_database"\n\n# Move data older than 60 days from table A to history table B\nmysql -u $USER -p$PASSWORD $DATABASE -e "\nINSERT INTO table_B (columns...)\nSELECT columns...\nFROM table_A\nWHERE created_at < NOW() - INTERVAL 60 DAY;\nDELETE FROM table_A\nWHERE created_at < NOW() - INTERVAL 60 DAY;"\n\n# Delete data older than 365 days from history table B\nmysql -u $USER -p$PASSWORD $DATABASE -e "\nDELETE FROM table_B\nWHERE created_at < NOW() - INTERVAL 365 DAY;"