#!/bin/bash\n\n   PACKAGE_NAME="MY_PACKAGE"\n   SCHEMA_NAME="MY_SCHEMA"\n\n   sqlplus -s /nolog <<EOF\n   CONNECT username/password@database\n   SET HEADING OFF\n   SET ECHO OFF\n   SET FEEDBACK OFF\n   SET PAGESIZE 0\n   SET LONG 1000000\n\n   SPOOL ${PACKAGE_NAME}_spec.sql\n   SELECT DBMS_METADATA.GET_DDL('PACKAGE_SPEC', '${PACKAGE_NAME}', '${SCHEMA_NAME}') FROM DUAL;\n   SPOOL OFF\n\n   SPOOL ${PACKAGE_NAME}_body.sql\n   SELECT DBMS_METADATA.GET_DDL('PACKAGE_BODY', '${PACKAGE_NAME}', '${SCHEMA_NAME}') FROM DUAL;\n   SPOOL OFF\n   EXIT\n   EOF\n\n   git add ${PACKAGE_NAME}_spec.sql ${PACKAGE_NAME}_body.sql\n   git commit -m "Updated ${PACKAGE_NAME}"