#!/bin/bash\n\n# Variables\nREPO_URL="svn://your-svn-repo-url/path/to/repo"\nWORKDIR="/path/to/temp/working/directory"\nLIVE_SITE_DIR="/path/to/live/site/directory"\nREVISION="HEAD"\n\n# Export the latest revision from SVN\nsvn export --force $REPO_URL@$REVISION $WORKDIR\n\n# Sync with the live site directory\nrsync -avz --delete $WORKDIR/ $LIVE_SITE_DIR/\n\n# Clean up the working directory\nrm -rf $WORKDIR\n\necho "Deployment to live site complete."