#!/bin/bash\n\n# Convert the UTF-16 file to UTF-8\niconv -f UTF-16 -t UTF-8 input_utf16.txt > temp_utf8.txt\n\n# Perform the substitution using sed on the UTF-8 file\nsed 's/search_pattern/replacement_pattern/g' temp_utf8.txt > temp_utf8_modified.txt\n\n# Convert the modified UTF-8 file back to UTF-16\niconv -f UTF-8 -t UTF-16 temp_utf8_modified.txt > output_utf16.txt\n\n# Clean up temporary files\nrm temp_utf8.txt temp_utf8_modified.txt