# Install OpenSSL and development tools\n   brew install openssl\n\n   # Set up the environment variables\n   export LDFLAGS="-L/usr/local/opt/openssl/lib"\n   export CPPFLAGS="-I/usr/local/opt/openssl/include"\n   export PKG_CONFIG_PATH="/usr/local/opt/openssl/lib/pkgconfig"\n\n   # Download and extract PHP source code\n   curl -O https://www.php.net/distributions/php-5.2.1.tar.gz\n   tar -xzvf php-5.2.1.tar.gz\n   cd php-5.2.1\n\n   # Configure PHP with OpenSSL\n   ./configure --with-openssl=/usr/local/opt/openssl --with-apxs2=/usr/sbin/apxs\n\n   # Compile and install PHP\n   make\n   sudo make install